$(function () { $('body').off('keypress.enter').on('keypress.enter', function (e) { if (e.which == 13 && e.target.type != 'textarea') { var $btn = $(e.target).closest('.form').find('.default-button'); if ($btn.length > 0) { if ($btn[0].type == 'submit' || $btn[0].type == 'button') { $btn[0].click() } else { eval($btn[0].href) } return false } } }) });